mapper.insert:
insert into cloudpassinfo (CloudPassNo, Password, Name, 
  CloudPassKind, Phone, IdNo, 
  DisableFlag, CreateUser, CreateTime, 
  UpdateUser, UpdateTime, Version
  )
values (#{cloudPassNo,jdbcType=BIGINT}, #{password,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{cloudPassKind,jdbcType=VARCHAR}, #{phone,jdbcType=VARCHAR}, #{idNo,jdbcType=VARCHAR}, 
  #{disableFlag,jdbcType=INTEGER}, #{createUser,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{updateUser,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}, #{version,jdbcType=INTEGER}
  )

mapper.select:
select
cloud.CloudPassNo, cloud.Password, cloud.Name, cloud.CloudPassKind, cloud.Phone,
cloud.IdNo, cloud.DisableFlag, cloud.CreateUser,
cloud.CreateTime, cloud.UpdateUser, cloud.UpdateTime, cloud.Version
from cloudpassinfo cloud join userrole ur on cloud.CloudPassNo = ur.CloudPassNo
and ur.RoleNo = #{roleNo,jdbcType=BIGINT}
and cloud.Phone = #{phone,jdbcType=VARCHAR}
and cloud.Password = #{password,jdbcType=VARCHAR}

select * from cloudpassinfo c , drugstore d where
c.CloudPassNo = d.CloudPassNo
and
c.Phone = #{phone,jdbcType=VARCHAR}
and
d.DrugStoreNo != #{drugStoreNo,jdbcType=BIGINT}

select * from cloudpassinfo cpi, ptinfo pt where cpi.CloudPassNo = pt.CloudPassNo

  and cpi.Phone = #{phone,jdbcType=VARCHAR}


  and cpi.IdNo = #{idNo,jdbcType=VARCHAR}

and pt.PtNo != #{ptNo,jdbcType=BIGINT}

select * from cloudpassinfo cpi, drinfo dr ,userrole ur where cpi.CloudPassNo = dr.CloudPassNo
and ur.CloudPassNo = cpi.CloudPassNo

  and cpi.Phone = #{phone,jdbcType=VARCHAR}


  and cpi.IdNo = #{idCard,jdbcType=VARCHAR}

and dr.drNo !=  #{drNo,jdbcType=BIGINT}

select * from cloudpassinfo cpi,drugstore ds
where ds.CloudPassNo = cpi.CloudPassNo
and ds.DrugStoreNo = #{drugStoreNo,jdbcType=BIGINT}

select * from cloudpassinfo cpi,userrole ur,disinfo dis where cpi.CloudPassNo = ur.CloudPassNo
and dis.CloudPassNo = cpi.CloudPassNo
and (ur.roleNo = 5 or ur.roleNo = 7)

  and dis.disNo != #{disNo,jdbcType=BIGINT}

and cpi.Phone = #{phone,jdbcType=VARCHAR}

select cpi.* from cloudpassinfo cpi right join userrole ur on cpi.CloudPassNo = ur.CloudPassNo
where
cpi.Phone = #{phone,jdbcType=VARCHAR}
