mapper.select:
select 

from commonmaster
where BigCategoryNo = #{bigCategoryNo,jdbcType=INTEGER}
  and SmallCategoryNo = #{smallCategoryNo,jdbcType=VARCHAR}

mapper.delete:
delete from commonmaster
where BigCategoryNo = #{bigCategoryNo,jdbcType=INTEGER}
  and SmallCategoryNo = #{smallCategoryNo,jdbcType=VARCHAR}
