mapper.select:
select * from diseasemaster where 1=1

  and Keyword1 like "%"#{keyword1,jdbcType=VARCHAR}"%"


  and ChtType = #{chtType,jdbcType=VARCHAR}

select * from diseasemaster where
    IcdCode = #{icdCode,jdbcType=VARCHAR} and ChtName = #{chtName,jdbcType=VARCHAR}
    and DNo != #{DNo,jdbcType=BIGINT}
