mapper.select:
select 

from mallorder
where MallNo = #{mallNo,jdbcType=BIGINT}

select
mao.MallNo, mao.PtNo, mao.DrNo, mao.DisNo, mao.DrugStoreNo, mao.MedOrderNo, mao.OrderStatus, mao.OrderTime, mao.Phone,
mao.Address, mao.PayStatus, mao.PayTime, mao.ShippingStatus, mao.ShippingCompany, mao.ShippingContext, mao.ShippingNo,
mao.ShippingTime, mao.OrderAmount, mao.CreateUser, mao.CreateTime, mao.UpdateUser, mao.UpdateTime, mao.Version,
mao.Rental, mao.ActivityDetail, mao.OffMoney, mao.Remark,mao.PacketId
from mallorder mao,medorder meo
where mao.MedOrderNo = meo.OrderNo

  and mao.PtNo = #{ptNo,jdbcType=BIGINT}


  and mao.DrNo = #{drNo,jdbcType=BIGINT}


  and mao.MallNo = #{mallNo,jdbcType=BIGINT}


  and mao.OrderStatus = #{orderStatus,jdbcType=INTEGER}

select mao.MallNo, mao.PtNo, mao.DrNo, mao.DisNo, mao.DrugStoreNo, mao.MedOrderNo, mao.OrderStatus, mao.OrderTime, mao.Phone, mao.Address, mao.PayStatus, mao.PayTime, mao.ShippingStatus, mao.ShippingCompany, mao.ShippingContext, mao.ShippingNo, mao.ShippingTime, mao.OrderAmount, mao.CreateUser, mao.CreateTime, mao.UpdateUser, mao.UpdateTime, mao.Version , mao.Rental, mao.ActivityDetail, mao.OffMoney, mao.Remark,mao.PacketId from mallorder mao,medorder meo,medrecord mer where mao.MedOrderNo = meo.OrderNo and mer.MedRecordNo = meo.MedRecordNo
and meo.OrderNo = #{medOrderNo,jdbcType=BIGINT}
and mer.MedRecordNo = #{medRecordNo,jdbcType=BIGINT}

select * from mallorder
where OrderStatus !=3
and PayStatus = 0
and (Version = 1 or Version = 3 or Version = 4 or Version = 5   )
and PtNo = #{ptNo,jdbcType=BIGINT}

  and OrderStatus = #{orderStatus,jdbcType=INTEGER}

select
mao.MallNo, mao.PtNo, mao.DrNo, mao.DisNo, mao.DrugStoreNo, mao.MedOrderNo, mao.OrderStatus, mao.OrderTime, mao.Phone,
mao.Address, mao.PayStatus, mao.PayTime, mao.ShippingStatus, mao.ShippingCompany, mao.ShippingContext, mao.ShippingNo,
mao.ShippingTime, mao.OrderAmount, mao.CreateUser, mao.CreateTime, mao.UpdateUser, mao.UpdateTime, mao.Version
, mao.Rental, mao.ActivityDetail, mao.OffMoney, mao.Remark,mao.PacketId
from mallorder mao,medorder meo
where mao.MedOrderNo = meo.OrderNo and mao.OrderStatus !=3

  and mao.PtNo = #{ptNo,jdbcType=BIGINT}


  and mao.DrNo = #{drNo,jdbcType=BIGINT}


  and mao.MallNo = #{mallNo,jdbcType=BIGINT}


  and mao.OrderStatus = #{orderStatus,jdbcType=INTEGER}


  and OrderTime between #{startDate,jdbcType=VARCHAR} and #{endDate,jdbcType=VARCHAR}

select
mao.MallNo, mao.PtNo, mao.DrNo, mao.DisNo, mao.DrugStoreNo, mao.MedOrderNo, mao.OrderStatus, mao.OrderTime, mao.Phone,
mao.Address, mao.PayStatus, mao.PayTime, mao.ShippingStatus, mao.ShippingCompany, mao.ShippingContext, mao.ShippingNo,
mao.ShippingTime, mao.OrderAmount, mao.CreateUser, mao.CreateTime, mao.UpdateUser, mao.UpdateTime, mao.Version
, mao.Rental, mao.ActivityDetail, mao.OffMoney, mao.Remark,mao.PacketId
from mallorder mao,medorder meo,disinfo dis,drdisrelation drdis
where mao.MedOrderNo = meo.OrderNo
and mao.DrNo = drdis.DrNo
and dis.DisNo = drdis.DisNo
and mao.OrderStatus !=3
and mao.PayStatus = 0

and
(select disNo from disinfo where CloudPassNo =
(select DisLeader from disinfo where disNo = dis.DisNo)) = #{disNo1,jdbcType=BIGINT}


and
dis.DisNo = #{disNo2,jdbcType=BIGINT}


  and mao.MallNo = #{mallNo,jdbcType=BIGINT}


  and mao.DrugStoreNo = #{drugStoreNo,jdbcType=BIGINT}


  and meo.OrderStatus = #{orderStatus,jdbcType=INTEGER}


  and date_format(mao.OrderTime,'%Y-%m') between #{startDate,jdbcType=VARCHAR} and #{endDate,jdbcType=VARCHAR}

select distinct
mao.PtNo
from mallorder mao,cloudpassinfo cpi,ptinfo pt
where mao.PtNo = pt.PtNo and cpi.CloudPassNo = pt.CloudPassNo

  and mao.DrNo = #{drNo,jdbcType=BIGINT}


  and cpi.phone = #{phone,jdbcType=VARCHAR}


  and cpi.Name = #{name,jdbcType=VARCHAR}

select mao.MallNo, mao.PtNo, mao.DrNo, mao.DisNo, mao.DrugStoreNo, mao.MedOrderNo, mao.OrderStatus, mao.OrderTime, mao.Phone, mao.Address, mao.PayStatus, mao.PayTime, mao.ShippingStatus, mao.ShippingCompany, mao.ShippingContext, mao.ShippingNo, mao.ShippingTime, mao.OrderAmount, mao.CreateUser, mao.CreateTime, mao.UpdateUser, mao.UpdateTime, mao.Version , mao.Rental, mao.ActivityDetail, mao.OffMoney, mao.Remark,mao.PacketId from mallorder mao,cloudpassinfo cpi,ptinfo pt where mao.PtNo = pt.PtNo and cpi.CloudPassNo = pt.CloudPassNo
and mao.DrNo = #{drNo,jdbcType=BIGINT}
and (cpi.phone = #{name,jdbcType=VARCHAR} or cpi.name = #{name,jdbcType=VARCHAR})
and mao.OrderTime between #{startDate,jdbcType=VARCHAR} and #{endDate,jdbcType=VARCHAR}

select cpi.Name as drName, ds.CommonName as drugName, sum(mei.number) as sum, sum(meo.OrderAmount) as amountSum from mallorder mao,cloudpassinfo cpi,ptinfo pt,medorder meo,meditem mei,drugset ds where mao.PtNo = pt.PtNo and cpi.CloudPassNo = pt.CloudPassNo and meo.OrderNo = mao.MedOrderNo and mei.MedOrderNo = meo.OrderNo and mao.PayStatus = 0 and ds.DrugSetNo = mei.drugNo and meo.orderType = 2
and mao.DrNo = #{drNo,jdbcType=BIGINT}
and (cpi.phone = #{name,jdbcType=VARCHAR} or cpi.name = #{name,jdbcType=VARCHAR})
and date_format(meo.UpdateTime,'%Y-%m') = #{startDate,jdbcType=TIMESTAMP}
and mei.drugNo = #{drugNo,jdbcType=BIGINT}
group by cpi.Name,drugName

select cpi.Name as drName, ds.CommonName as drugName, sum(meo.PrescriptionNum) as sum, sum(meo.OrderAmount) as amountSum from mallorder mao,cloudpassinfo cpi,ptinfo pt,medorder meo,meditem mei,drugset ds where mao.PtNo = pt.PtNo and cpi.CloudPassNo = pt.CloudPassNo and meo.OrderNo = mao.MedOrderNo and mei.MedOrderNo = meo.OrderNo and mao.PayStatus = 0 and ds.DrugSetNo = mei.drugNo and meo.orderType = 1
and mao.DrNo = #{drNo,jdbcType=BIGINT}
and (cpi.phone = #{name,jdbcType=VARCHAR} or cpi.name = #{name,jdbcType=VARCHAR})
and date_format(meo.UpdateTime,'%Y-%m') = #{startDate,jdbcType=TIMESTAMP}
and meo.drugNo = #{drugNo,jdbcType=BIGINT}
group by cpi.Name,drugName

select * from mallorder mao LEFT JOIN medorder meo on mao.MedOrderNo = meo.OrderNo LEFT JOIN drinfo dr on dr.drNo = mao.drNo LEFT JOIN drdisrelation drdis on dr.DrNo = drdis.DrNo LEFT JOIN cloudpassinfo cpi on cpi.CloudPassNo = dr.CloudPassNo where meo.OrderType = 1 and mao.PayStatus = 0
and cpi.Phone = #{drPhone,jdbcType=VARCHAR}
and cpi.Name = #{drName,jdbcType=VARCHAR}
and dr.DrNo = #{drNo,jdbcType=VARCHAR}
and meo.UpdateTime between #{startDate,jdbcType=TIMESTAMP} and #{endDate,jdbcType=TIMESTAMP}
and drdis.disNo in (select disNo from disinfo where disLeader = (select CloudPassNo from disinfo where disNo = #{disNo1,jdbcType=BIGINT}))
and drdis.disNo in (select disNo from disinfo where disLeader = (select CloudPassNo from disinfo where disNo = #{disNo1,jdbcType=BIGINT})) and drdis.disNo = #{disNo2,jdbcType=BIGINT}
and drdis.disNo = #{disNo2,jdbcType=BIGINT}

select mao.* from
mallorder mao,redpacketrecord d,redpacketrule e
where mao.PacketId = d.PacketId and e.PacketId = d.PacketRuleId
and mao.PacketId = #{packetId,jdbcType=BIGINT}
and mao.PayStatus = #{payStatus,jdbcType=INTEGER}
and mao.PtNo = #{ptNo,jdbcType=BIGINT}
